import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { worker } from '../api';

function WorkerDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    loadTasks();
  }, []);

  const loadTasks = async () => {
    try {
      const response = await worker.getTasks();
      setTasks(response.data.tasks);
    } catch (err) {
      console.error('Failed to load tasks', err);
    }
  };

  const handleUpdateStatus = async (taskId, status) => {
    try {
      await worker.updateTask(taskId, { status });
      loadTasks();
    } catch (err) {
      alert('Failed to update task');
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  return (
    <div>
      <div className="navbar">
        <h1>Worker Dashboard</h1>
        <div>
          <span style={{ marginRight: '20px' }}>Welcome, {user.name}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>

      <div className="dashboard">
        <h2>My Assigned Tasks</h2>

        <div className="complaints-list">
          {tasks.length === 0 ? (
            <p>No tasks assigned yet.</p>
          ) : (
            tasks.map((task) => (
              <div key={task.id} className="complaint-card">
                <h4>{task.title}</h4>
                <p>{task.description}</p>
                <p><strong>Category:</strong> {task.category}</p>
                <p><strong>Location:</strong> {task.location}</p>
                <div>
                  <span className={`badge badge-${task.status}`}>{task.status}</span>
                  <span className={`badge badge-${task.priority}`}>{task.priority}</span>
                </div>
                {task.deadline && (
                  <p style={{ marginTop: '10px', color: '#e74c3c' }}>
                    <strong>Deadline:</strong> {new Date(task.deadline).toLocaleString()}
                  </p>
                )}
                <div style={{ marginTop: '15px' }}>
                  {task.status === 'assigned' && (
                    <button 
                      className="btn btn-primary" 
                      style={{ width: 'auto', marginRight: '10px' }}
                      onClick={() => handleUpdateStatus(task.id, 'in_progress')}
                    >
                      Start Work
                    </button>
                  )}
                  {task.status === 'in_progress' && (
                    <button 
                      className="btn btn-primary" 
                      style={{ width: 'auto' }}
                      onClick={() => handleUpdateStatus(task.id, 'completed')}
                    >
                      Mark as Completed
                    </button>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default WorkerDashboard;
