      }

      // Automatically set location to "Hostel - Room Number"
      const autoLocation = `${user.hostel || 'Hostel'} - Room ${user.room_number || 'N/A'}`;

      const complaintData = {
        title: title,
        description: formData.description,
        category: formData.wing,
        location: autoLocation,
        hostel: user.hostel
