          {stages.map((stage, index) => {
            const isActive = index <= currentStage;
            const isCurrent = index === currentStage;

            return (
              <div key={stage.key} style={{ 
                flex: 1, 
                display: 'flex', 
                flexDirection: 'column', 
                alignItems: 'center',
                position: 'relative',
                zIndex: 1
