import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { complaints } from '../api';

function StudentDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [complaintsList, setComplaintsList] = useState([]);
  const [activeView, setActiveView] = useState('menu'); // menu, file, pending, resolved
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    area: '',
    subArea: '',
    issue: '',
    subIssue: '',
    description: '',
    location: '',
  });

  const complaintStructure = {
    'Room': {
      'Left-Wing': {
        'Corridor-1': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-2': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-3': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-4': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-5': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Washroom': ['Water Leakage', 'Water Supply', 'Water Heater', 'Lights Issue', 'Shower Issue', 'Door Issue', 'Toilet Issue', 'Others']
      },
      'Right-Wing': {
        'Corridor-1': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-2': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-3': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-4': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-5': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Washroom': ['Water Leakage', 'Water Supply', 'Water Heater', 'Lights Issue', 'Shower Issue', 'Door Issue', 'Toilet Issue', 'Others']
      },
      'Extension': {
        'Corridor-1': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-2': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-3': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-4': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Corridor-5': ['Lights Issue', 'Cleaning Issue', 'Washing machine', 'Water Cooler', 'Others'],
        'Washroom': ['Water Leakage', 'Water Supply', 'Water Heater', 'Lights Issue', 'Shower Issue', 'Door Issue', 'Toilet Issue', 'Others']
      }
    },
    'Common Area': {
      'Ground': ['Cleaning Issue', 'Water Stoppage', 'Others'],
      'TV-Hall': {
        'Section-1': ['Chairs Issue', 'Lights Issue', 'Fans Issue', 'TV Issue', 'AC Issue', 'Others'],
        'Section-2': ['Chairs Issue', 'Lights Issue', 'Fans Issue', 'TV Issue', 'AC Issue', 'Others'],
        'Section-3': ['Chairs Issue', 'Lights Issue', 'Fans Issue', 'TV Issue', 'AC Issue', 'Others'],
        'Section-4': ['Chairs Issue', 'Lights Issue', 'Fans Issue', 'TV Issue', 'AC Issue', 'Others']
      },
      'Games Area': ['Equipment Missing/Damaged', 'Lights Issue', 'Fans Issue', 'Others']
    }
  };

  useEffect(() => {
    console.log('StudentDashboard mounted, user:', user);
    loadComplaints();
  }, []);

  const loadComplaints = async () => {
    try {
      console.log('Loading complaints...');
      const response = await complaints.getAll();
      console.log('Complaints loaded:', response.data);
      setComplaintsList(response.data.complaints);
    } catch (err) {
      console.error('Failed to load complaints', err);
      console.error('Error details:', err.response?.data);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const complaintData = {
        title: `${formData.area} - ${formData.subArea} - ${formData.issue}`,
        description: `${formData.subIssue ? formData.subIssue + ': ' : ''}${formData.description}`,
        category: formData.issue,
        location: formData.location
      };
      await complaints.create(complaintData);
      setShowModal(false);
      setFormData({ area: '', subArea: '', issue: '', subIssue: '', description: '', location: '' });
      loadComplaints();
      alert('Complaint filed successfully!');
      setActiveView('pending');
    } catch (err) {
      alert('Failed to create complaint');
    }
  };

  const handleEmergency = () => {
    setFormData({ 
      area: 'Emergency',
      subArea: 'Emergency',
      issue: 'Emergency',
      subIssue: 'URGENT',
      description: 'EMERGENCY - ',
      location: '' 
    });
    setShowModal(true);
  };

  const handleAreaChange = (area) => {
    setFormData({ ...formData, area, subArea: '', issue: '', subIssue: '' });
  };

  const handleSubAreaChange = (subArea) => {
    setFormData({ ...formData, subArea, issue: '', subIssue: '' });
  };

  const handleIssueChange = (issue) => {
    setFormData({ ...formData, issue, subIssue: '' });
  };

  const getPendingComplaints = () => {
    return complaintsList.filter(c => ['pending', 'validated', 'assigned', 'in_progress'].includes(c.status));
  };

  const getResolvedComplaints = () => {
    return complaintsList.filter(c => ['completed', 'resolved'].includes(c.status));
  };

  const getRecentComplaints = () => {
    return complaintsList
      .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
      .slice(0, 5);
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  if (!user || !user.name) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h2>Loading...</h2>
        <p>If this persists, please <button onClick={() => navigate('/')}>return to home</button></p>
      </div>
    );
  }

  return (
    <div>
      <div className="navbar">
        <h1>VNIT Grievance System</h1>
        <div>
          <span style={{ marginRight: '20px' }}>Welcome, {user.name}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>

      <div className="dashboard">
        {activeView === 'menu' && (
          <div className="student-menu">
            <h2 style={{ textAlign: 'center', marginBottom: '2rem', color: '#333' }}>Student Dashboard</h2>
            <div className="menu-grid">
              <div className="menu-card" onClick={() => setShowModal(true)}>
                <div className="menu-icon">üìù</div>
                <h3>File a Complaint</h3>
                <p>Submit new grievance</p>
              </div>

              <div className="menu-card" onClick={() => setActiveView('pending')}>
                <div className="menu-icon">‚è≥</div>
                <h3>View Pending</h3>
                <p>Ongoing complaints</p>
                {getPendingComplaints().length > 0 && <span className="badge-count">{getPendingComplaints().length}</span>}
              </div>

              <div className="menu-card" onClick={() => setActiveView('resolved')}>
                <div className="menu-icon">‚úÖ</div>
                <h3>View Resolved</h3>
                <p>Completed complaints</p>
                {getResolvedComplaints().length > 0 && <span className="badge-count">{getResolvedComplaints().length}</span>}
              </div>

              <div className="menu-card emergency-card" onClick={handleEmergency}>
                <div className="menu-icon">üö®</div>
                <h3>Emergency</h3>
                <p>Report urgent issue</p>
              </div>
            </div>

            <div className="recent-complaints">
              <h3>Recently Filed Complaints</h3>
              {getRecentComplaints().length === 0 ? (
                <p style={{ color: '#999', textAlign: 'center', padding: '2rem' }}>No complaints filed yet.</p>
              ) : (
                <div>
                  {getRecentComplaints().map((complaint) => (
                    <div key={complaint.id} className="complaint-card">
                      <h4>{complaint.title}</h4>
                      <p>{complaint.description}</p>
                      <p><strong>Category:</strong> {complaint.category}</p>
                      <p><strong>Location:</strong> {complaint.location}</p>
                      <div>
                        <span className={`badge badge-${complaint.status.replace('_', '-')}`}>{complaint.status.replace('_', ' ')}</span>
                        <span className={`badge badge-${complaint.priority}`}>{complaint.priority} priority</span>
                        {complaint.is_emergency && <span className="badge badge-high">EMERGENCY</span>}
                      </div>
                      <p style={{ marginTop: '10px', fontSize: '12px', color: '#999' }}>
                        Filed: {new Date(complaint.created_at).toLocaleString()}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {activeView === 'pending' && (
          <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
              <h2>Pending Complaints</h2>
              <button className="btn btn-secondary" onClick={() => setActiveView('menu')}>Back to Menu</button>
            </div>
            <div className="complaints-list">
              {getPendingComplaints().length === 0 ? (
                <p>No pending complaints.</p>
              ) : (
                getPendingComplaints().map((complaint) => (
                  <div key={complaint.id} className="complaint-card">
                    <h4>{complaint.title}</h4>
                    <p>{complaint.description}</p>
                    <p><strong>Category:</strong> {complaint.category}</p>
                    <p><strong>Location:</strong> {complaint.location}</p>
                    <div>
                      <span className={`badge badge-${complaint.status.replace('_', '-')}`}>{complaint.status.replace('_', ' ')}</span>
                      <span className={`badge badge-${complaint.priority}`}>{complaint.priority} priority</span>
                      {complaint.is_emergency && <span className="badge badge-high">EMERGENCY</span>}
                    </div>
                    <p style={{ marginTop: '10px', fontSize: '12px', color: '#999' }}>
                      Filed: {new Date(complaint.created_at).toLocaleString()}
                    </p>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeView === 'resolved' && (
          <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
              <h2>Resolved Complaints</h2>
              <button className="btn btn-secondary" onClick={() => setActiveView('menu')}>Back to Menu</button>
            </div>
            <div className="complaints-list">
              {getResolvedComplaints().length === 0 ? (
                <p>No resolved complaints.</p>
              ) : (
                getResolvedComplaints().map((complaint) => (
                  <div key={complaint.id} className="complaint-card">
                    <h4>{complaint.title}</h4>
                    <p>{complaint.description}</p>
                    <p><strong>Category:</strong> {complaint.category}</p>
                    <p><strong>Location:</strong> {complaint.location}</p>
                    <div>
                      <span className={`badge badge-${complaint.status.replace('_', '-')}`}>{complaint.status.replace('_', ' ')}</span>
                      <span className={`badge badge-${complaint.priority}`}>{complaint.priority} priority</span>
                    </div>
                    <p style={{ marginTop: '10px', fontSize: '12px', color: '#999' }}>
                      Filed: {new Date(complaint.created_at).toLocaleString()}
                    </p>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>File New Complaint</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Area *</label>
                <select
                  value={formData.area}
                  onChange={(e) => handleAreaChange(e.target.value)}
                  required
                >
                  <option value="">-- Select Area --</option>
                  <option value="Room">Room</option>
                  <option value="Common Area">Common Area</option>
                </select>
              </div>

              {formData.area && (
                <div className="form-group">
                  <label>Sub Area *</label>
                  <select
                    value={formData.subArea}
                    onChange={(e) => handleSubAreaChange(e.target.value)}
                    required
                  >
                    <option value="">-- Select Sub Area --</option>
                    {Object.keys(complaintStructure[formData.area] || {}).map(subArea => (
                      <option key={subArea} value={subArea}>{subArea}</option>
                    ))}
                  </select>
                </div>
              )}

              {formData.subArea && (
                <div className="form-group">
                  <label>Issue Type *</label>
                  <select
                    value={formData.issue}
                    onChange={(e) => handleIssueChange(e.target.value)}
                    required
                  >
                    <option value="">-- Select Issue --</option>
                    {Array.isArray(complaintStructure[formData.area][formData.subArea]) 
                      ? complaintStructure[formData.area][formData.subArea].map(issue => (
                          <option key={issue} value={issue}>{issue}</option>
                        ))
                      : Object.keys(complaintStructure[formData.area][formData.subArea] || {}).map(issue => (
                          <option key={issue} value={issue}>{issue}</option>
                        ))
                    }
                  </select>
                </div>
              )}

              {formData.issue && !Array.isArray(complaintStructure[formData.area][formData.subArea]) && complaintStructure[formData.area]?.[formData.subArea]?.[formData.issue] && (
                <div className="form-group">
                  <label>Specific Issue *</label>
                  <select
                    value={formData.subIssue}
                    onChange={(e) => setFormData({ ...formData, subIssue: e.target.value })}
                    required
                  >
                    <option value="">-- Select Specific Issue --</option>
                    {complaintStructure[formData.area][formData.subArea][formData.issue].map(subIssue => (
                      <option key={subIssue} value={subIssue}>{subIssue}</option>
                    ))}
                  </select>
                </div>
              )}

              <div className="form-group">
                <label>Additional Details</label>
                <textarea
                  rows="4"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Provide additional details about the issue..."
                />
              </div>

              <div className="form-group">
                <label>Location *</label>
                <input
                  type="text"
                  value={formData.location}
                  onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                  placeholder="e.g., Room 101, Floor 2"
                  required
                />
              </div>

              <button type="submit" className="btn btn-primary">Submit Complaint</button>
              <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default StudentDashboard;
