import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { admin } from '../api';

function AdminDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const [complaintsList, setComplaintsList] = useState([]);
  const [stats, setStats] = useState({});
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    loadData();
  }, [filter]);

  const loadData = async () => {
    try {
      const params = filter !== 'all' ? { status: filter } : {};
      const response = await admin.getAllComplaints(params);
      setComplaintsList(response.data.complaints);

      const statsResponse = await admin.getDashboard();
      setStats(statsResponse.data.stats);
    } catch (err) {
      console.error('Failed to load data', err);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div>
      <div className="navbar">
        <h1>Admin Dashboard</h1>
        <div>
          <span style={{ marginRight: '20px' }}>
            {user.name} ({stats.role || 'admin'})
          </span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>

      <div className="dashboard">
        <div className="stats">
          <div className="stat-card">
            <h3>Pending Validation</h3>
            <div className="number">{stats.pending_validation || 0}</div>
          </div>
          <div className="stat-card">
            <h3>Pending Assignment</h3>
            <div className="number">{stats.pending_assignment || 0}</div>
          </div>
          <div className="stat-card">
            <h3>In Progress</h3>
            <div className="number">{stats.in_progress || 0}</div>
          </div>
          <div className="stat-card">
            <h3>Escalated</h3>
            <div className="number">{stats.escalated_complaints || 0}</div>
          </div>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{ marginRight: '10px' }}>Filter:</label>
          <select value={filter} onChange={(e) => setFilter(e.target.value)} style={{ padding: '8px' }}>
            <option value="all">All Complaints</option>
            <option value="pending">Pending</option>
            <option value="validated">Validated</option>
            <option value="assigned">Assigned</option>
            <option value="in_progress">In Progress</option>
            <option value="escalated">Escalated</option>
          </select>
        </div>

        <div className="complaints-list">
          <h3>Complaints</h3>
          {complaintsList.length === 0 ? (
            <p>No complaints found.</p>
          ) : (
            complaintsList.map((complaint) => (
              <div key={complaint.id} className="complaint-card">
                <h4>{complaint.title}</h4>
                <p>{complaint.description}</p>
                <p><strong>Category:</strong> {complaint.category}</p>
                <p><strong>Location:</strong> {complaint.location}</p>
                <div>
                  <span className={`badge badge-${complaint.status}`}>{complaint.status}</span>
                  <span className={`badge badge-${complaint.priority}`}>{complaint.priority}</span>
                  {complaint.is_emergency && <span className="badge badge-high">EMERGENCY</span>}
                </div>
                <p style={{ marginTop: '10px', fontSize: '12px', color: '#999' }}>
                  Filed: {new Date(complaint.created_at).toLocaleString()}
                </p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
